	/**
	 * Populates the object using an array.
	 *
	 * This is particularly useful when populating an object from one of the
	 * request arrays (e.g. $_POST).  This method goes through the column
	 * names, checking to see whether a matching key exists in populated
	 * array. If so the setByName() method is called for that column.
	 *
	 * You can specify the key type of the array by additionally passing one
	 * of the class type constants BasePeer::TYPE_PHPNAME, BasePeer::TYPE_STUDLYPHPNAME,
	 * BasePeer::TYPE_COLNAME, BasePeer::TYPE_FIELDNAME, BasePeer::TYPE_NUM.
	 * The default key type is the column's phpname (e.g. 'AuthorId')
	 *
	 * @param      array  $arr     An array to populate the object from.
	 * @param      string $keyType The type of keys the array uses.
	 * @return     void
	 */
	public function fromArray($arr, $keyType = BasePeer::TYPE_PHPNAME)
	{
		$keys = EvaluationPeer::getFieldNames($keyType);

		if (array_key_exists($keys[0], $arr)) $this->setSiteId($arr[$keys[0]]);
		if (array_key_exists($keys[1], $arr)) $this->setEcriteriaId($arr[$keys[1]]);
		if (array_key_exists($keys[2], $arr)) $this->setMetricId($arr[$keys[2]]);
		if (array_key_exists($keys[3], $arr)) $this->setValue($arr[$keys[3]]);
		if (array_key_exists($keys[4], $arr)) $this->setCreatedAt($arr[$keys[4]]);
		if (array_key_exists($keys[5], $arr)) $this->setUpdatedAt($arr[$keys[5]]);
	}

	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(EvaluationPeer::DATABASE_NAME);

		if ($this->isColumnModified(EvaluationPeer::SITE_ID)) $criteria->add(EvaluationPeer::SITE_ID, $this->site_id);
		if ($this->isColumnModified(EvaluationPeer::ECRITERIA_ID)) $criteria->add(EvaluationPeer::ECRITERIA_ID, $this->ecriteria_id);
		if ($this->isColumnModified(EvaluationPeer::METRIC_ID)) $criteria->add(EvaluationPeer::METRIC_ID, $this->metric_id);
		if ($this->isColumnModified(EvaluationPeer::VALUE)) $criteria->add(EvaluationPeer::VALUE, $this->value);
		if ($this->isColumnModified(EvaluationPeer::CREATED_AT)) $criteria->add(EvaluationPeer::CREATED_AT, $this->created_at);
		if ($this->isColumnModified(EvaluationPeer::UPDATED_AT)) $criteria->add(EvaluationPeer::UPDATED_AT, $this->updated_at);

		return $criteria;
	}

	/**
	 * Builds a Criteria object containing the primary key for this object.
	 *
	 * Unlike buildCriteria() this method includes the primary key values regardless
	 * of whether or not they have been modified.
	 *
	 * @return     Criteria The Criteria object containing value(s) for primary key(s).
	 */
	public function buildPkeyCriteria()
	{
		$criteria = new Criteria(EvaluationPeer::DATABASE_NAME);
		$criteria->add(EvaluationPeer::SITE_ID, $this->site_id);
		$criteria->add(EvaluationPeer::ECRITERIA_ID, $this->ecriteria_id);
		$criteria->add(EvaluationPeer::METRIC_ID, $this->metric_id);

		return $criteria;
	}

