	/**
	 * Performs an INSERT on the database, given a Evaluation or Criteria object.
	 *
	 * @param      mixed $values Criteria or Evaluation object containing data that is used to create the INSERT statement.
	 * @param      PropelPDO $con the PropelPDO connection to use
	 * @return     mixed The new primary key.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doInsert($values, PropelPDO $con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(EvaluationPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from Evaluation object
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
			$con->commit();
		} catch(PropelException $e) {
			$con->rollBack();
			throw $e;
		}

		return $pk;
	}

	/**
	 * Performs an UPDATE on the database, given a Evaluation or Criteria object.
	 *
	 * @param      mixed $values Criteria or Evaluation object containing data that is used to create the UPDATE statement.
	 * @param      PropelPDO $con The connection to use (specify PropelPDO connection object to exert more control over transactions).
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doUpdate($values, PropelPDO $con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(EvaluationPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		$selectCriteria = new Criteria(self::DATABASE_NAME);

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity

			$comparison = $criteria->getComparison(EvaluationPeer::SITE_ID);
			$value = $criteria->remove(EvaluationPeer::SITE_ID);
			if ($value) {
				$selectCriteria->add(EvaluationPeer::SITE_ID, $value, $comparison);
			} else {
				$selectCriteria->setPrimaryTableName(EvaluationPeer::TABLE_NAME);
			}

			$comparison = $criteria->getComparison(EvaluationPeer::ECRITERIA_ID);
			$value = $criteria->remove(EvaluationPeer::ECRITERIA_ID);
			if ($value) {
				$selectCriteria->add(EvaluationPeer::ECRITERIA_ID, $value, $comparison);
			} else {
				$selectCriteria->setPrimaryTableName(EvaluationPeer::TABLE_NAME);
			}

			$comparison = $criteria->getComparison(EvaluationPeer::METRIC_ID);
			$value = $criteria->remove(EvaluationPeer::METRIC_ID);
			if ($value) {
				$selectCriteria->add(EvaluationPeer::METRIC_ID, $value, $comparison);
			} else {
				$selectCriteria->setPrimaryTableName(EvaluationPeer::TABLE_NAME);
			}

		} else { // $values is Evaluation object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
		}

		// set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		return BasePeer::doUpdate($selectCriteria, $criteria, $con);
	}

	/**
	 * Deletes all rows from the evaluation table.
	 *
	 * @param      PropelPDO $con the connection to use
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	public static function doDeleteAll(PropelPDO $con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(EvaluationPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}
		$affectedRows = 0; // initialize var to track total num of affected rows
		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += BasePeer::doDeleteAll(EvaluationPeer::TABLE_NAME, $con, EvaluationPeer::DATABASE_NAME);
			// Because this db requires some delete cascade/set null emulation, we have to
			// clear the cached instance *after* the emulation has happened (since
			// instances get re-added by the select statement contained therein).
			EvaluationPeer::clearInstancePool();
			EvaluationPeer::clearRelatedInstancePool();
			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * Performs a DELETE on the database, given a Evaluation or Criteria object OR a primary key value.
	 *
	 * @param      mixed $values Criteria or Evaluation object or primary key or array of primary keys
	 *              which is used to create the DELETE statement
	 * @param      PropelPDO $con the connection to use
	 * @return     int 	The number of affected rows (if supported by underlying database driver).  This includes CASCADE-related rows
	 *				if supported by native driver or if emulated using Propel.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	 public static function doDelete($values, PropelPDO $con = null)
	 {
		if ($con === null) {
			$con = Propel::getConnection(EvaluationPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			// invalidate the cache for all objects of this type, since we have no
			// way of knowing (without running a query) what objects should be invalidated
			// from the cache based on this Criteria.
			EvaluationPeer::clearInstancePool();
			// rename for clarity
			$criteria = clone $values;
		} elseif ($values instanceof Evaluation) { // it's a model object
			// invalidate the cache for this single object
			EvaluationPeer::removeInstanceFromPool($values);
			// create criteria based on pk values
			$criteria = $values->buildPkeyCriteria();
		} else { // it's a primary key, or an array of pks
			$criteria = new Criteria(self::DATABASE_NAME);
			// primary key is composite; we therefore, expect
			// the primary key passed to be an array of pkey values
			if (count($values) == count($values, COUNT_RECURSIVE)) {
				// array is not multi-dimensional
				$values = array($values);
			}
			foreach ($values as $value) {
				$criterion = $criteria->getNewCriterion(EvaluationPeer::SITE_ID, $value[0]);
				$criterion->addAnd($criteria->getNewCriterion(EvaluationPeer::ECRITERIA_ID, $value[1]));
				$criterion->addAnd($criteria->getNewCriterion(EvaluationPeer::METRIC_ID, $value[2]));
				$criteria->addOr($criterion);
				// we can invalidate the cache for this single PK
				EvaluationPeer::removeInstanceFromPool($value);
			}
		}

		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$affectedRows = 0; // initialize var to track total num of affected rows

		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			
			$affectedRows += BasePeer::doDelete($criteria, $con);
			EvaluationPeer::clearRelatedInstancePool();
			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * Validates all modified columns of given Evaluation object.
	 * If parameter $columns is either a single column name or an array of column names
	 * than only those columns are validated.
	 *
	 * NOTICE: This does not apply to primary or foreign keys for now.
	 *
	 * @param      Evaluation $obj The object to validate.
	 * @param      mixed $cols Column name or array of column names.
	 *
	 * @return     mixed TRUE if all columns are valid or the error message of the first invalid column.
	 */
	public static function doValidate($obj, $cols = null)
	{
		$columns = array();

		if ($cols) {
			$dbMap = Propel::getDatabaseMap(EvaluationPeer::DATABASE_NAME);
			$tableMap = $dbMap->getTable(EvaluationPeer::TABLE_NAME);

			if (! is_array($cols)) {
				$cols = array($cols);
			}

			foreach ($cols as $colName) {
				if ($tableMap->containsColumn($colName)) {
					$get = 'get' . $tableMap->getColumn($colName)->getPhpName();
					$columns[$colName] = $obj->$get();
				}
			}
		} else {

		}

		return BasePeer::doValidate(EvaluationPeer::DATABASE_NAME, EvaluationPeer::TABLE_NAME, $columns);
	}

	/**
	 * Retrieve object using using composite pkey values.
	 * @param      int $site_id
	 * @param      int $ecriteria_id
	 * @param      int $metric_id
	 * @param      PropelPDO $con
	 * @return     Evaluation
	 */
	public static function retrieveByPK($site_id, $ecriteria_id, $metric_id, PropelPDO $con = null) {
		$_instancePoolKey = serialize(array((string) $site_id, (string) $ecriteria_id, (string) $metric_id));
 		if (null !== ($obj = EvaluationPeer::getInstanceFromPool($_instancePoolKey))) {
 			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(EvaluationPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}
		$criteria = new Criteria(EvaluationPeer::DATABASE_NAME);
		$criteria->add(EvaluationPeer::SITE_ID, $site_id);
		$criteria->add(EvaluationPeer::ECRITERIA_ID, $ecriteria_id);
		$criteria->add(EvaluationPeer::METRIC_ID, $metric_id);
		$v = EvaluationPeer::doSelect($criteria, $con);

		return !empty($v) ? $v[0] : null;
	}
